<script>
export default {
  name: 'NavbarComponent',
  data() {
    return {
      navItems: [
        {name: 'Présentation', routeName: 'home', sectionId: 'presentation'},
        {name: 'Entreprise', routeName: 'home', sectionId: 'entreprise'},
        {name: 'Stage', routeName: 'home', sectionId: 'stage'},
        {name: 'Compétences', routeName: 'home', sectionId: 'competences'},
      ],
      isVisible: false,
      animationClass: '',

      isAnimating: false,
      animationTimeout: null,
    };
  },
  methods: {
    toggleScale() {
      if (!this.isVisible) {
        this.isVisible = true;
        document.body.style.overflow = 'hidden';
        this.animationClass = 'slide-down-fade-in';

        setTimeout(() => {
          this.animationClass = '';
        }, 1300);
      }
    },

    unsetScale() {
      if (this.isVisible) {
        this.animationClass = 'slide-up-fade-out';
        document.body.style.overflow = '';

        setTimeout(() => {
          this.isVisible = false;
          this.animationClass = '';
        }, 1300);
      }
    },

    startAnim() {
      if (this.animationTimeout) {
        clearTimeout(this.animationTimeout);
        this.animationTimeout = null;
      }
      this.isAnimating = true;
    },

    stopAnim() {
      this.animationTimeout = setTimeout(() => {
        this.isAnimating = false;
        this.animationTimeout = null;
      }, 1700);
    },
  },
};
</script>

<style>
.transition-font-weight {
  font-variation-settings: 'wght' 400;
  transition: font-variation-settings 0.3s ease;
}

.transition-font-weight:hover {
