}


@keyframes slideUpFadeOut {
  from {
    transform: translateY(0);
    opacity: 1;
  }
  to {
    transform: translateY(-100%);
    opacity: 0;
  }
}

@keyframes slideDownFadeIn {
  from {
    transform: translateY(-100%);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.slide-up-fade-out {
  animation: slideUpFadeOut 1.3s ease-out forwards;
}

.slide-down-fade-in {
  animation: slideDownFadeIn 1.3s ease-out forwards;
}


@keyframes floatRotateScale {
  0%, 100% {
    transform: translateX(0) rotate(0deg) scale(1);
  }
  25% {
    transform: translateX(5px) rotate(3deg) scale(1.1);
  }
  50% {
    transform: translateX(0) rotate(0deg) scale(1);
  }
  75% {
